import * as React from 'react'
import Request from 'utils/Request';
import { DesError } from '../decorators'
@DesError
export default class github extends React.Component<any, any> {
    state = {
        stars: "0"
    }
    async componentDidMount() {
        const str = await Request.ajax("/_framework/GetGithubStarts").toPromise();
        this.setState({ stars: str });
    }
    render() {
        return (
            <div>
                <a href="https://github.com/dotnetcore/wtm" target="_blank" style={{
                    backgroundColor: "#eee",
                    backgroundImage: "linear-gradient(to bottom, #fcfcfc 0, #eee 100%)",
                    backgroundRepeat: "no-repeat",
                    border: "1px solid #d5d5d5",
                    color: "#333",
                    paddingLeft: 5,
                    paddingRight: 5
                }}>
                    <span style={{
                        fontSize: 16,
                        boxSizing: "border-box",
                        backgroundSize: "100% 100%",
                        backgroundPositionY: -2,
                        backgroundRepeat: "no-repeat",
                        backgroundImage: "url('data:image/svg+xml;base64,')"
                    }}>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span>
                    <span>&nbsp;Stars {this.state.stars}
                    </span>
                </a>
            </div>
        );
    }

}